<script>
	var QQMapWX = require('../../common/qqmap-wx-jssdk.js');
	var demo = new QQMapWX({
	    key:'4N5BZ-J5EC6-RT4S7-M3YC6-CQUOJ-XUFU6' // 必填
	});
	export default {
		data() {
			return {
				swiperlist: [],
				resourceId:'' ,//详情id,
				detailInfo:{} //闲情信息
			}
		},
		methods: {
			getRende() {
				console.log("执行")
				var that=this
				uni.showLoading({
					title:"加载中"
				})
				uni.request({
					url:"http://192.168.101.13:9999/person/queryDetail",
					method:'POST',
					header:{
						"Content-Type":"application/json"
					},
					data:{
						resourceId:that.resourceId
					},
					success(res) {
						if(res.data.status==200) {
							uni.hideLoading()
							that.detailInfo=res.data.data[0]
						}else {
							uni.showToast({
								title:res.data.message,
								icon:'none'
							})
						}
					},
					fail(err) {
						console.log(err)
					}
				})  
			},
			add() {
			 wx.openLocation({
			      latitude:this.detailInfo.lat,
			      longitude:this.detailInfo.lng,
			      scale: 18,
			      name: this.detailInfo.personName,
			      address: this.detailInfo.companyAddr
			    })
			},
			
		},
		onLoad(options) {
			console.log(options)
			this.resourceId=options.resourceId
			this.getRende()
		}
	}
</script>
